from unittest import TestCase, mock

from src.domain.pricing_updates.carrefour_scraping import CarrefourScraping


class CarrefourScrapingTest(TestCase):
    def test_get_price(self):
        url = 'https://www.carrefour.com.br/Margarina-Cremosa-com-Sal-Qualy-500g/p/149730'
        http_request_mock = mock.Mock()
        http_request_mock.get.return_value = self.__get_content()
        price = CarrefourScraping(http_request_mock).get_price(url)
        self.assertEqual(price, 4.99)

